import './Home.css';
import ScrollAnimation from 'react-animate-on-scroll';
import Product from './../../components/Products/Products';
import data from './../data';
import { Link } from 'react-router-dom';
import { onError } from "./../../libs/errorLibs";
import React, { useState, useEffect } from "react";
import { useAppContext } from "./../../libs/contextLibs";
import { API } from 'aws-amplify'

function Home() {

    const [Listproducts, setProducts] = useState([]);
    const { isAuthenticated } = useAppContext();

    useEffect(() => {
        async function onLoad() {
            try {
                const product = await loadProducts();
                setProducts(product.product);
            } catch (e) {
                onError(e);
            }
        }

        onLoad();

    }, [true]);

    function loadProducts() {
        return API.get("products", "/products");
    }

    console.log(Listproducts);
    /* 
                        {products.map((id, name, createdAt) => (

                            // <Product
                            //     name={product.name}
                            //     image={product.image}
                            //     description={product.description}
                            //     price={product.price}
                            //     rating={product.rating}
                            //     id={product._id}
                            // />
                            <h1>
                                {name.trim().split("\n")[0]}
                            </h1>

                        ))} */



    return (
        <>
            <div className="header">
                <ScrollAnimation animateIn='fadeIn'>
                    <div className="box-download">
                        <p className="box-download-text">Eles provam que maldade não tem tamanho </p>
                        <p className="box-download-text">Meu <span className="box-download-text2">Minion</span> favorito </p>
                    </div>
                </ScrollAnimation>
            </div>
            <div className="content">
                <ul className="products">
                    {products.map((id, name, createdAt) => (

                        // <Product
                        //     name={product.name}
                        //     image={product.image}
                        //     description={product.description}
                        //     price={product.price}
                        //     rating={product.rating}
                        //     id={product._id}
                        // />
                        <h1>
                            {name.trim().split("\n")[0]}
                        </h1>

                    ))}
                </ul>
            </div>

            <footer className="foot">
                <p>Todos os direitos de imagem são de seus respectivos autores.</p>
            </footer>
        </>
    );

}

export default Home;
