import './Product.css'
import { onError } from "./../../libs/errorLibs";
import React, { useState, useEffect } from "react";
import { useAppContext } from "./../../libs/contextLibs";
import { API } from 'aws-amplify'
import ScrollAnimation from 'react-animate-on-scroll';
import Modal from 'react-bootstrap/Modal'

function Product(props) {

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    const [Listproducts, setProducts] = useState([]);
    const { isAuthenticated } = useAppContext();
    useEffect(() => {
        async function onLoad() {
            try {
                const product = await loadProducts();
                setProducts(product.product);
            } catch (e) {
                onError(e);
            }
        }

        onLoad();

    }, [true]);

    function loadProducts() {
        return API.get("products", "/products");
    }
    //const product = Listproducts.find(({id}) === props.match.params.id);
    const _id = props.match.params.id


    return (
        <>



            {Listproducts.map(({ id, name, s3ImageUrl, price, description }) => (
                id === _id &&

                (

                    <>

                        <Modal
                            show={show}
                            onHide={handleClose}
                            backdrop="static"
                            keyboard={false}
                        >
                            <Modal.Header closeButton>
                                <Modal.Title>Reserva de produto</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>
                                O seu produto: {name}, foi reservado com sucesso!
                            </Modal.Body>
                            <Modal.Footer>
                                <Button variant="warning" onClick={handleClose}>
                                    Fechar
                                </Button>
                            </Modal.Footer>
                        </Modal>
                        <ScrollAnimation animateIn='fadeIn'>
                            <main className="container">

                                <div className="left-column">

                                    <img image="black" alt="" src={s3ImageUrl} />
                                </div>
                                <div className="right-column">

                                    <div className="product-description">
                                        <span>Minion</span>
                                        <h1>{name}</h1>
                                        <p>{description}</p>
                                    </div>

                                    <div className="product-price">
                                        <span>R${price}</span>

                                        {isAuthenticated ?
                                            <>
                                                <a href="#" className="cart-btn"  onClick={handleShow}>Reserva</a>

                                            </> : <a href="/login" className="cart-btn">Login necessário</a>}

                                    </div>
                                </div>

                            </main>
                        </ScrollAnimation>
                    </>
                )


            ))}
        </>
    )
}

export default Product;
