import './Product.css'
import { onError } from "./../../libs/errorLibs";
import React, { useState, useEffect } from "react";
import { useAppContext } from "./../../libs/contextLibs";
import { API } from 'aws-amplify'
import ScrollAnimation from 'react-animate-on-scroll';
import { useHistory } from "react-router-dom"

function Product(props) {

    const [show, setShow] = useState(false);

    const handleClose = () =>{
        history.push("/")
        setShow(false)
    } ;
    const handleShow = () => setShow(true);

    const history = useHistory();
    const [Listproducts, setProducts] = useState([]);
    const { isAuthenticated } = useAppContext();
    useEffect(() => {
        async function onLoad() {
            try {
                const product = await loadProducts();
                setProducts(product.product);
            } catch (e) {
                onError(e);
            }
        }

        onLoad();

    }, [true]);

    function loadProducts() {
        return API.get("products", "/products");
    }
    //const product = Listproducts.find(({id}) === props.match.params.id);
    const _id = props.match.params.id


    return (
        <>



            {Listproducts.map(({ id, name, s3ImageUrl, price, description }) => (
                id === _id &&

                (

                    <>

                        <ScrollAnimation animateIn='fadeIn'>
                            <main className="container">

                                <div className="left-column">

                                    <img image="black" alt="" src={s3ImageUrl} />
                                </div>
                                <div className="right-column">

                                    <div className="product-description">
                                        <span>Minion</span>
                                        <h1>{name}</h1>
                                        <p>{description}</p>
                                    </div>

                                    <div className="product-price">
                                        <span>R${price}</span>

                                        {isAuthenticated ?
                                            <>
                                                <a href="#" className="cart-btn"  onClick={handleShow}>Reserva</a>
                                                {show && 

                                                    <div className="modal"  onClick={handleClose}>
                                                        <div className="boxModal">
                                                            <button className="close"  onClick={handleClose}/>
                                                            <div className="contentModal">
                                                            <img src="https://images6.fanpop.com/image/photos/40700000/Minions-despicable-me-minions-40777767-320-320.gif"/>
                                                                Seu produto {name} foi reservado com sucesso! 
                                                            </div>
                                                            {}
                                                        </div>
                                                    </div>

                                                }

                                            </> : <a href="/login" className="cart-btn">Login necessário</a>}

                                    </div>
                                </div>

                            </main>
                        </ScrollAnimation>
                    </>
                )


            ))}
        </>
    )
}

export default Product;
