import React, { useState } from 'react';
import './Registro.css';
import { useHistory } from "react-router-dom";
import ScrollAnimation from 'react-animate-on-scroll';

import LoaderButton from "./../../components/LoaderButton/LoaderButton";
import Form from "react-bootstrap/Form";
import { useFormFields } from "./../../libs/hooksLib";

import { onError } from "./../../libs/errorLibs";
import { Auth } from "aws-amplify";
import { useAppContext } from "./../../libs/contextLibs";


export default function Login() {

    const [fields, handleFieldChange] = useFormFields({
        email: "",
        password: "",
        confirmPassword: "",
        confirmationCode: "",
    });
    const history = useHistory();
    const [newUser, setNewUser] = useState(null);
    const { userHasAuthenticated } = useAppContext();
    const [isLoading, setIsLoading] = useState(false);

    function validateForm() {
        return (
            fields.email.length > 0 &&
            fields.password.length > 0 &&
            fields.password === fields.confirmPassword
        );
    }

    function validateConfirmationForm() {
        return fields.confirmationCode.length > 0;
    }

    async function handleSubmit(event) {
        event.preventDefault();

        setIsLoading(true);

        setNewUser("test");

        setIsLoading(false);
    }

    async function handleConfirmationSubmit(event) {
        event.preventDefault();

        setIsLoading(true);
    }


    function renderConfirmationForm() {
        return (
            <>

                <div className="split left">
                    <a href="/" className="back">
                        Volta para loja
                </a>
                    <div className="centered">
                        <div className="box">
                            <ScrollAnimation animateIn='zoomIn'>
                                <h2 className="tilteForm">
                                    Registro
                                </h2>
                                <Form onSubmit={handleConfirmationSubmit}>
                                    <Form.Group controlId="confirmationCode" size="lg">
                                        <Form.Label>Confirmation Code</Form.Label>
                                        <Form.Control
                                            autoFocus
                                            type="tel"
                                            onChange={handleFieldChange}
                                            value={fields.confirmationCode}
                                        />
                                        <Form.Text muted>Por favor verifique seu email para ver o código de confirmação</Form.Text>
                                    </Form.Group>
                                    <LoaderButton
                                        block
                                        size="lg"
                                        type="submit"
                                        variant="success"
                                        isLoading={isLoading}
                                        disabled={!validateConfirmationForm()}
                                    >
                                        Verify
            </LoaderButton>
                                </Form>
                            </ScrollAnimation>
                        </div>

                    </div>
                </div>
                <div className="split right"></div>






            </>
        );
    }

    function renderForm() {
        return (
            <>
                <div className="split left">
                    <a href="/" className="back">
                        Volta para loja
                </a>
                    <div className="centered">
                        <div className="box">
                            <ScrollAnimation animateIn='zoomIn'>
                                <h2 className="tilteForm">
                                    Registro
                                </h2>
                                <Form onSubmit={handleSubmit}>
                                    <Form.Group controlId="email" size="lg">
                                        <Form.Label>Email</Form.Label>
                                        <Form.Control
                                            autoFocus
                                            type="email"
                                            value={fields.email}
                                            onChange={handleFieldChange}
                                        />
                                    </Form.Group>
                                    <Form.Group controlId="password" size="lg">
                                        <Form.Label>Password</Form.Label>
                                        <Form.Control
                                            type="password"
                                            value={fields.password}
                                            onChange={handleFieldChange}
                                        />
                                    </Form.Group>
                                    <Form.Group controlId="confirmPassword" size="lg">
                                        <Form.Label>Confirm Password</Form.Label>
                                        <Form.Control
                                            type="password"
                                            onChange={handleFieldChange}
                                            value={fields.confirmPassword}
                                        />
                                    </Form.Group>
                                    <LoaderButton className="btn" type="submit" disabled={!validateForm()}>
                                        Registrar
                                </LoaderButton>
                                    <a className="linkBack" href="/login"><p>Já tem conta?</p></a>
                                </Form>
                            </ScrollAnimation>
                        </div>

                    </div>
                </div>
                <div className="split right"></div>

            </>
        );
    }

    return (
        <>


        </>

    );

}
