import './Product.css'
import { onError } from "./../../libs/errorLibs";
import React, { useState, useEffect } from "react";
import { useAppContext } from "./../../libs/contextLibs";
import { API } from 'aws-amplify'
import ScrollAnimation from 'react-animate-on-scroll';
import  Alert  from 'react-bootstrap/Alert';

function Product(props) {

    const [visible, setVisible] = useState(false);
    const [Listproducts, setProducts] = useState([]);
    const { isAuthenticated } = useAppContext();

    useEffect(() => {
        async function onLoad() {
            try {
                const product = await loadProducts();
                setProducts(product.product);
            } catch (e) {
                onError(e);
            }
        }

        onLoad();

    }, [true]);

    function loadProducts() {
        return API.get("products", "/products");
    }
    //const product = Listproducts.find(({id}) === props.match.params.id);
    const _id = props.match.params.id


    return (
        <>
            {Listproducts.map(({ id, name, s3ImageUrl, price, description }) => (
                id === _id &&

                (
                    <ScrollAnimation animateIn='fadeIn'>
                        <main class="container">

                            <div class="left-column">

                                <img image="black" alt="" src={s3ImageUrl} />
                            </div>
                            <div class="right-column">

                                <div class="product-description">
                                    <span>Minion</span>
                                    <h1>{name}</h1>
                                    <p>{description}</p>
                                </div>

                                <div class="product-price">
                                    <span>R${price}</span>

                                    {isAuthenticated ?
                                        <>
                                            <a href="#" onClick={setVisible(true)} class="cart-btn">Reserva</a>
                                            <Alert color="warning" isOpen={visible} toggle={setVisible(false)}>
                                                Reserva feita com sucesso!
                                            </Alert>
                                        </> : <a href="/login" class="cart-btn">Login necess√°rio</a>}

                                </div>
                            </div>

                        </main>
                    </ScrollAnimation>
                )


            ))}
        </>
    )
}

export default Product;
